import React, { useRef } from 'react';
import Hero from './components/Hero';
import Services from './components/Services';
import Process from './components/Process';
import Gallery from './components/Gallery';
import Stats from './components/Stats';
import Testimonials from './components/Testimonials';
import Pricing from './components/Pricing';
import CTA from './components/CTA';
import Footer from './components/Footer';
import { AppBar, Box, Button, Container, Toolbar } from '@mui/material';

export default function App() {
  const ctaRef = useRef(null);
  const scrollToCTA = () => ctaRef.current?.scrollIntoView({ behavior: 'smooth' });

  return (
    <Box>
      <AppBar position="fixed" color="transparent" sx={{ backdropFilter: 'blur(6px)', bgcolor: 'rgba(15,23,42,0.6)' }}>
        <Toolbar>
          <Container maxWidth="lg" sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <img src="/logo.png" alt="Logo" style={{ height: 28 }} />
            <Button variant="contained" color="primary" onClick={scrollToCTA}>Reservar</Button>
          </Container>
        </Toolbar>
      </AppBar>
      <Box sx={{ mt: 8 }}>
        <Hero onReserveClick={scrollToCTA} />
        <Services />
        <Process />
        <Gallery />
        <Stats />
        <Testimonials />
        <Pricing />
        <div ref={ctaRef}><CTA /></div>
        <Footer />
      </Box>
    </Box>
  );
}
